import { ADD_TODO_ITEM, CHANGE_INPUT_VALUE } from "./actionTypes";

export const getInputChangeAction = (value) => ({
  // type里面描述做什么事（这里是告诉store改变input value）
  type: CHANGE_INPUT_VALUE,
  value
});

export const getAddItemAction = (value) => ({
  // type里面描述做什么事（这里是告诉store改变input value）
  type: ADD_TODO_ITEM,
});
