import React, { Component, Fragment } from "react";
import TodoItem from "./TodoItem";
import './style.css';

class TodoList extends Component {
  // constructor的优先级最高
  constructor(props) {
    super(props);
    // state 负责存储component里面的数据
    this.state = {
      inputValue: "", // 这是程序初始化的时候input value的值
      list: []
    }
    this.handleInputChange = this.handleInputChange.bind(this)
    this.handleBtnClick = this.handleBtnClick.bind(this)
    this.handleItemDelete = this.handleItemDelete.bind(this)
  }
  render() {
    return (
      // Fragment可以代替div 从而不影响布局
      <Fragment>
        <div>
         <label htmlFor="insertArea">Input here</label>
          <input
            // 当render执行的时候，input里面的value被渲染成了this.state.inputValue的值
            value={this.state.inputValue}
            // 当你在Input框里输入内容的时候，onChange就会被执行，并且事件绑定的时候需要用 bind(this)对函数的作用域进行变更
            onChange={this.handleInputChange}
            /> 
          <button onClick={this.handleBtnClick}> Submit </button>
        </div>
        <ul>
          {this.getTodoItem()}
        </ul>
      </Fragment>
    );
  }
  getTodoItem() {
    return this.state.list.map((item, index) => {
      return (
          <TodoItem 
            key={index}
            content={item} 
            index={index}
            deleteItem={this.handleItemDelete}
          /> 
      )
    })
  }

  handleInputChange(e) {
    // 如果想改变State里面的数据项的内容，不能直接更改，需要通过setState来进行变更
    // 你在Input框里输入的什么内容可以从e.target.value获取到，并用setState把InputValue的值设置成你输入的内容，随之页面也会改变
    const value = e.target.value;
		this.setState(() => ({
			inputValue: value
		}));
  }
  handleBtnClick() {
    this.setState((prevState) => ({
			list: [...prevState.list, prevState.inputValue],
			inputValue: ''
		}));
  }
  handleItemDelete(index) {
    // 需要利用const先拷贝出一个副本，不能直接修改State里的内容，因为immutable特性
    this.setState((prevState) => {
			const list = [...prevState.list];
			list.splice(index, 1);
			return {list}
		});
  }
}

export default TodoList;
// export default把该组件导出
