import styled from 'styled-components';

export const DetailWrapper = styled.div`
  overflow: hidden;
  width: 620px;
  margin: 0 auto;
  padding-bottom: 100px;
`

export const Header = styled.div`
  margin: 50px 0 20px 0;
  line-height: 44px;
  font-style: 34px;
  color: #333;
  font-weight: 700;
`

export const Content = styled.div`
  color: #2f2f2f;
`
