import React, { Component, Fragment } from 'react';

class TodoList extends Component {
  // constructor的优先级最高
  constructor(props) {
    super(props);
    this.state = {
      // 这是程序初始化的时候input value的值
      inputValue:'',
      list: []
    }
  }

  render() {
    return (
      // Fragment可以代替div 从而不影响布局
      <Fragment>
        <div>
          <input 
          // 当render执行的时候，input里面的value被渲染成了this.state.inputValue的值
           value={this.state.inputValue}
          // 当你在Input框里输入内容的时候，onChange就会被执行
           onChange={this.handleInputChange.bind(this)} 
          />
          <button>Submit</button>
        </div>
        <ul>
          <li> Learning English </li>
          <li> Learning coding</li>
        </ul>
      </Fragment>
    );
  }
  handleInputChange(e) {
    this.setState ({
      // 你在Input框里输入的什么内容可以从e.target.value获取到，并用setState把InputValue的值设置成你输入的内容
      inputValue: e.target.value
    })
  }
}

export default TodoList;
// export default把该组件导出
