import React, { Component, Fragment } from 'react';

class TodoList extends Component {
  // constructor的优先级最高
  constructor(props) {
    super(props);
    // state 负责存储component里面的数据
    this.state = {
      inputValue:'', // 这是程序初始化的时候input value的值
      list: ['hello','nnn']
    }
  }

  render() {
    return (
      // Fragment可以代替div 从而不影响布局
      <Fragment>
        <div>
          <input 
          // 当render执行的时候，input里面的value被渲染成了this.state.inputValue的值
           value={this.state.inputValue}
          // 当你在Input框里输入内容的时候，onChange就会被执行，并且事件绑定的时候需要用 bind(this)对函数的作用域进行变更
           onChange={this.handleInputChange.bind(this)} //JSX里面如果需要用JS的表达式，需要用大括号包起来
          />
          <button onClick={this.handleBtnClick.bind(this)}> Submit </button>
        </div>
        <ul>
          {
            this.state.list.map((item, index) => {
              return <li>{item}</li>
            })
          }
        </ul>
      </Fragment>
    );
  }
  handleInputChange(e) {
    // 如果想改变State里面的数据项的内容，不能直接更改，需要通过setState来进行变更
    this.setState ({
      inputValue: e.target.value
      // 你在Input框里输入的什么内容可以从e.target.value获取到，并用setState把InputValue的值设置成你输入的内容，随之页面也会改变
    })
  }
  handleBtnClick() {
    this.setState ({
      list: [...this.state.list, this.state.inputValue],
    })
  }
}

export default TodoList;
// export default把该组件导出
