import React from 'react'
import { Input, InputWrap } from './styles/login';

export default class Login extends React.Component{
  constructor(props){
    super(props);
    this.state={
      username:'',
      password:'',
      errorMessage:'',
      successMessage:''
    }
  }
  setValue(e){
    let id = e.target.id;
    let value = e.target.value;
    let username = this.state.username;
    if(value === undefined || value === '')
    return;
    if(id === 'username'){
      username = value.toUpperCase();
      this.setState({
        username:username
      })
    }
    else{
      this.setState({
        password:e.target.value
      })
    }
  }
  submit(e){
    // to check if username and password entered are undefined
    if(this.state.username === undefined || this.state.username === ''
        || this.state.password === undefined || this.state.password === ''){
          this.setState({
            errorMessage:'Enter both the field details'
          })
        }
        else{
          this.setState({
            successMessage:'Welcome admin'
          })
        }
  }
  render() {
    return (
      <InputWrap>
        Username:<Input type='text' id='username' value={this.state.username} onChange={(e)=>this.setValue(e)}/>
        Password:<Input type='password' id='password' value={this.state.password} onChange={(e)=>this.setValue(e)}/>
        <p>{this.state.errorMessage}</p>
        <p>{this.state.successMessage}</p>
        <Button onClick={(e)=>this.submit(e)}>Submit</Button>
        <Button><a href='/'>Cancel</a></Button>
      </InputWrap>
    )
  }
}
