import React, { Component,Fragment } from 'react';
import JiejieItem from './JiejieItem'

class Jiejie extends Component {
  //生命周期：在某一时刻可以自动执行的函数
  constructor(props){
    super(props)
    this.state={
      inputValue:'',
      list:['Head Massage','Foot Massage']
    }
  }

  componentWillMount(){
    console.log('componentWillMount-----组件将要挂载到页面的时候');
  }
  componentDidMount(){
    console.log('componentDidMount-----组件挂载完成的时刻');
  }
  render (){
    return (
      <Fragment>
        <div>
            <input 
                value={this.state.inputValue} 
                onChange={this.inputChange.bind(this)}
                ref = {(input)=>{this.input=input}}
            />
            <button onClick={this.addList.bind(this)}> Add Service </button>
        </div>
        <ul>
            {
              this.state.list.map((item,index)=>{
                return (
                      <div>
                          <JiejieItem
                            key={index+item} 
                            content={item}
                            index={index}
                            deleteItem={this.deleteItem.bind(this)}
                          />
                      </div>
                )
              }) 
            }  
        </ul>
      </Fragment>
    )
 }

  inputChange(){
   this.setState({
     inputValue: this.input.value
   })
  }
  addList(){
    this.setState({
      list:[...this.state.list, this.state.inputValue]
    })
  }

  deleteItem(index){
    let newList = this.state.list
    newList.splice(index,1)
    this.setState({
      list:newList
    })
  }
}

export default Jiejie
